mapper.select:
select movie_id
from movie_category_rel
where deleted = 0
  and category_id = #{categoryId}
order by create_time desc, id desc
    limit #{limit}

SELECT r.movie_id AS movieId, c.name AS categoryName FROM movie_category_rel r JOIN movie_category c ON c.id = r.category_id AND c.deleted = 0 WHERE r.deleted = 0 AND r.movie_id IN
#{id}
ORDER BY r.movie_id ASC, c.sort ASC, c.id ASC
